const FormioComponentsIndex = {
  address: AddressComponent,
  base: BaseComponent,
  content: ContentComponent,
  container: ContainerComponent,
  datagrid: DataGridComponent,
  datetime: DateTimeComponent,
  day: DayComponent,
  htmlelement: HTMLComponent,
  hidden: HiddenComponent,
  editgrid: EditGridComponent,
  form: FormComponent,
  textfield: TextFieldComponent,
  phoneNumber: PhoneNumberComponent,
  email: EmailComponent,
  time: TimeComponent,
  checkbox: CheckBoxComponent,
  currency: CurrencyComponent,
  fieldset: FieldsetComponent,
  signature: SignatureComponent,
  select: SelectComponent,
  resource: ResourceComponent,
  textarea: TextAreaComponent,
  button: ButtonComponent,
  number: NumberComponent,
  password: PasswordComponent,
  panel: PanelComponent,
  column: ColumnComponent,
  columns: ColumnsComponent,
  table: TableComponent,
  unknown: UnknownComponent,
  radio: RadioComponent,
  selectboxes: SelectBoxesComponent,
  survey: SurveyComponent,
  well: WellComponent,
  gmap: GmapComponent,
  file: FileComponent,
  create: function(component, options, data, nobuild) {
    let comp = null;
    if (!component.type) {
      return null;
    }
    else if (this.hasOwnProperty(component.type)) {
      comp = new this[component.type](component, options, data);
    }
    else {
      comp = new UnknownComponent(component, options, data);
    }
    if (!nobuild) {
      comp.build();
      comp.isBuilt = true;
    }
    return comp;
  }
};

module.exports = FormioComponentsIndex;
export default FormioComponentsIndex;

