      }
      catch (err) {
        return cookies.erase('formioUser', {path: '/'});
      }
    }
    // iOS in private browse mode will throw an error but we can't detect ahead of time that we are in private mode.
    try {
      localStorage.setItem('formioUser', JSON.stringify(user));
